import React from 'react';
import { Spinner } from 'react-bootstrap';
function Loader() {
    return (
        <Spinner 
            animation = "border"
            role = "status"
            style = {{
                display : 'block',
                height  : '100px',
                width   : '100px',
                margin  : 'auto'
            }}
        >
            <span className="sr-only">Loading</span>
        </Spinner>
    )
}

export default Loader
