import products from '../products';

function ProductScreen({match}) {
    const product = products.find((product) => product._id === match.params.id );
    return (
        <div>
            <Link to='/' className='btn btn-light my-3'>
                Go Back
            </Link>
        </div>
    )
}

export default ProductScreen
